#define		On													0						 //--亮灯
#define		Off													1						 //--灭灯
//--定义开发板上的2个LED灯的借接口 ---------------------------------------
#define		RCC_APB2Periph_LED_PORT1    RCC_APB2Periph_GPIOB
#define 	LED_PORT1                		GPIOB
#define 	LEDUp_PIN						  		GPIO_Pin_0  //上侧LED
#define 	LEDDown_PIN						  		GPIO_Pin_1  //下侧LED

//--定义开发板上的2个LED灯的操作 -----------------------------------------
//--使用举例: Led_U(On);即可点亮Led    Led_U(Off);即可熄灭LED ------------

#define		LedRed(x)			(x == On)? 													   \
												GPIO_SetBits(LED_PORT1,LEDUp_PIN):     \
												GPIO_ResetBits(LED_PORT1,LEDUp_PIN)
							
#define		LedGreen(x)		  (x == On)? 													   \
												GPIO_SetBits(LED_PORT1,LEDDown_PIN):   \
												GPIO_ResetBits(LED_PORT1,LEDDown_PIN)
										

#define I2C_SDA           GPIO_Pin_6
#define I2C_SCL           GPIO_Pin_5

 
 												
